import { inject, onMounted, onUnmounted, ref } from "vue";
export default {
  name: "Home",
  setup: function () {
    let mqtt = ref(null);
    onMounted(() => {
      const mqttClient = inject('mqttClient');
      mqttClient.then(client => {
        client.on('connect', () => {
          mqtt = client;
          test(client);
        });
      }).catch(err => {
        console.log(err);
      });
    });
    onUnmounted(() => {
      console.log(mqtt);
      if (mqtt.value) {
        console.log("unmounted");
      }
    });
    const test = client => {
      client.subscribe('test');
      client.publish('test', 'lol');
    };
  }
};
