import {ref} from "vue"
import mqtt from "precompiled-mqtt";

/**
 * Asynchronous method that returns a promise, from which we can obtain the mqtt client if resolved.
 * We use the MQTT.js library to communicate with broker through websockets (Not TCP - that's why I used precompiled-mqtt)
 * @returns client
 */
const getMqttClient = async () => {
    const mqtt = require('precompiled-mqtt')

    const clientId = "SSB_interface"
    const host = 'ws://localhost:9001'

    const options = {
        keepalive: 60,
        clientId: clientId,
        protocolId: 'MQTT',
        protocolVersion: 4,
        clean: true,
        reconnectPeriod: 1000,
        connectTimeout: 30 * 1000,
        will: {
            topic: 'WillMsg',
            payload: 'Connection Closed abnormally..!',
            qos: 0,
            retain: false
        },
    }

    const client = await getClient(host, options)


    return client;
}

const getClient = (host, options) => {
    return new Promise((resolve, reject) => {
        const client = mqtt.connect(host, options)

        if(client){resolve(client)}
        else reject("Client connection couldn't be established ")
    })
}

export default getMqttClient
