import {ref} from "vue"
import mqtt from "precompiled-mqtt";

/**
 * Use the MQTT.js library to communicate with broker through websockets (Not TCP - that's why I used precompiled-mqtt)
 * @returns {{}}
 */
const getMqttClient = async () => {
    const mqtt = require('precompiled-mqtt')
    //const mqtt = require('mqtt')

    const clientId = 'mqttjs_' + Math.random().toString(16).substr(2, 8)
    const host = 'localhost:9001'

    const options = {
        keepalive: 60,
        clientId: clientId,
        protocolId: 'MQTT',
        protocolVersion: 4,
        clean: true,
        reconnectPeriod: 1000,
        connectTimeout: 30 * 1000,
        will: {
            topic: 'WillMsg',
            payload: 'Connection Closed abnormally..!',
            qos: 0,
            retain: false
        },
    }

    const client = await getClient(host, options)

    client.value.on('error', (err) => {
        console.log('Connection error: ', err)
    })
    client.value.on('reconnect', () => {
        console.log('Reconnecting...')
    })
    client.value.on('connect', () => {
        console.log('Connected to broker')
    })

    return client;
}

const getClient = (host, options) => {
    return new Promise((resolve, reject) => {
        const client = ref(mqtt.connect(host, options))

        if(client.value){resolve(client)}
        else reject("Client couldn't be established")
    })
}

export default getMqttClient
