/**
 * Method that extracts data from receivedData using the structure convention, then updates the values in sensorData
 * @param receivedData raw json received from mqtt broker
 * @param sensorData javascript json-like object reference to update
 */
function updateDataFromJSON(receivedData, sensorData) {
    const parsed = JSON.parse(receivedData)
    let component_name = ""
    let message_name = ""
    let field_name = ""

    try {
        parsed.forEach((message)=>{
            component_name = message["component_name"]
            message_name = message["message_name"]

            message["sensors"].forEach((sensor) => {
                field_name = sensor["field_name"]

                if(sensor["field_name"] === undefined) {throw new Error("Received JSON is not well formatted")}
                sensorData.value[component_name][message_name][field_name] = sensor["value"]
            })
        })
    }
    catch (err) {
        console.log(err)
    }
}

export default updateDataFromJSON
