import { inject, onMounted, ref, watch } from "vue";
export default {
  name: "Home",
  setup(props) {
    onMounted(() => {
      const mqttClient = inject('mqttClient');
      let mqtt = ref(null);
      mqttClient.then(client => {
        client.on('connect', () => {
          mqtt = client;
          console.log(mqtt);
          //subscribe('test')
        });
      }).catch(err => {
        console.log(err);
      });
      watch(mqtt, (newVal, oldVal) => {
        console.log("connected: " + mqtt.connected);
      });
    });
  }
};
