import {ref} from "vue";

/**
 * Use the MQTT.js library to communicate with broker through websockets (Not TCP - that's why I used precompiled-mqtt)
 * @returns {{}}
 */
const getMqttClient = async () => {
    //maybe need to use ref? i dont think

    let client = ref(null)

    const mqtt = require('precompiled-mqtt')
    const options = {
        // Clean session
        clean: true,
        connectTimeout: 4000,
        // Auth
        //clientId: 'emqx_test',
        //username: 'emqx_test',
        //password: 'emqx_test',
    }


    //const client  = mqtt.connect('mqtt://broker.emqx.io:1883', options)

    client  = await ref(mqtt.connect('mqtt://127.0.0.1:9001'))

    client.value.on('connect', function () {
        console.log('Connected')
        client.value.subscribe('test', function (err) {
            if (!err) {
                client.value.publish('test', 'Hello mqtt')
            }
        })
    })

    client.value.on('message', function (topic, message) {
        // message is Buffer
        console.log(message.toString())
    })

    return {client}
}

export default getMqttClient
